import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './home.css';

function Songresult(props){
    const navigate = useNavigate();

    const [reco, setReco] = useState([]);
    const [SelectedSongs, setSelectedSongs] = useState([]);
    const [SelectedIds, setSelectedIds] = useState([]);
    const [selectedLyrics, setSelectedLyrics] = useState(null);
    const [selectedTitle, setSelectedTitle] = useState(null);
    const [isModalVisible, setIsModalVisible] = useState(false);    

    const userId = JSON.parse(localStorage.getItem('user')).id;

    const getResult = async() => {
        try {
            const response = await axios.get(`http://localhost:8080/api/annoy/recommend-songs/${userId}`);
            setReco(response.data);
            console.log(response);
        } catch (e) {
            console.log(e);
        }
    };

    useEffect(() => {
        getResult();
      }, []);

      const handleSongClick = (song) => {
        setSelectedSongs((prevSongs) => {
          const newSongs = new Set(prevSongs); // 이전 선택된 아이템 집합 복사
            newSongs.add(song);
          return newSongs;
        });
      };

      const handleIdClick = (song) => {
        setSelectedIds((prevIds) => {
          const newIds = new Set(prevIds);
            newIds.add(song);
          return newIds;
        });
      };

      const handleLyricClick = (index) => {
        setSelectedLyrics(reco[index].lyric_Str);
        setSelectedTitle(reco[index].songName)
        setIsModalVisible(true);        
      };      

      const closeModal = () => {
        setIsModalVisible(false);
      };      
    

  
    return (
      
        <div className="cent" style={{ margin: "20px", width: "100%" }}>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <table className="td800">
            <tbody>
              <tr>
                <td colSpan="4" style={{ textAlign: "left" }}>
                  <p>Annoy모델을 기반으로 추천드립니다</p>
                  <p style={{fontSize : '14px'}}>
                  가사유사도를 이용하여 코사인유사도를 계산했습니다
                  </p>
                </td>
              </tr>
              {reco.length > 0 ? (
              reco.map((song, index) => (
                <tr key={index} style={{ fontSize: "16px", textAlign: "left" }}>
                  <td className="td400">
                    <div style={{ display: "flex", flexDirection: "row" }}>
                      <img
                        onClick={() => handleSongClick(song.songName)}
                        alt="song"
                        src={song.url}
                        width="50"
                        height="50"
                      />
                      <div style={{ marginLeft: "10px" }}>
                        <span onClick={() => handleSongClick(song.songName)}>
                          {song.songName}
                        </span>
                        <p
                          onClick={() => handleSongClick(song.songName)}
                          style={{ fontSize: "10px", textAlign: "left" }}
                        >
                          {song.artistName}
                        </p>
                      </div>
                    </div>
                  </td>
                  <td>
                  <div style={{ marginLeft : "100px", display: "flex", flexDirection: "row" }}>
                    <span style={{ cursor : 'pointer' }} onClick={() => handleLyricClick(index)} >
                    가사보기
                    </span>
                  </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4">로딩중입니다</td>
              </tr>
            )}              
              {/* <tr style={{ fontSize : '16px', textAlign: 'left' }} >
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}} >
                  <img onClick={() => {handleSongClick(reco[1].songName); handleIdClick(reco[1].id);}} alt = "song" src={reco[1].url} width="50" height="50"/>                  
                  <div style = {{ marginLeft : '10px' }} >
                  <span onClick={() => {handleSongClick(reco[1].songName); handleIdClick(reco[1].id);}}>
                    {reco[1].songName}
                  </span>
                  <p onClick={() => {handleSongClick(reco[1].songName); handleIdClick(reco[1].id);}} style={{ fontSize: '10px', textAlign: 'left' }} >{reco[1].artistName}</p>
                </div>
                </div>
              </td>
              </tr>
              <tr style={{ fontSize : '16px', textAlign: 'left' }}>
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}} >
                <img onClick={() => {handleSongClick(reco[2].songName); handleIdClick(reco[2].id);} }  alt = "song" src={reco[2].url} width="50" height="50"/>                         
                <div style = {{ marginLeft : '10px' }}>
                  <span onClick={() => {handleSongClick(reco[2].songName); handleIdClick(reco[2].id);}}>
                    {reco[2].songName}
                  </span>
                  <p onClick={() => {handleSongClick(reco[2].songName); handleIdClick(reco[2].id);}} style={{ fontSize: '10px', textAlign: 'left' }} >{reco[2].artistName}</p>                  
                </div>
                </div>                
              </td>
              </tr>
              <tr style={{ fontSize : '16px', textAlign: 'left' }} >
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}} >
                  <img onClick={() => {handleSongClick(reco[3].songName); handleIdClick(reco[3].id);}} alt = "song" src={reco[3].url} width="50" height="50"/>                  
                  <div style = {{ marginLeft : '10px' }} >
