                    {reco[3].songName}
                  </span>
                  <p onClick={() => {handleSongClick(reco[3].songName); handleIdClick(reco[3].id);}} style={{ fontSize: '10px', textAlign: 'left' }} >{reco[3].artistName}</p>
                </div>
                </div>
              </td>
              </tr>
              <tr style={{ fontSize : '16px', textAlign: 'left' }}>
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}} >
                <img onClick={() => {handleSongClick(reco[4].songName); handleIdClick(reco[4].id);} }  alt = "song" src={reco[4].url} width="50" height="50"/>                         
                <div style = {{ marginLeft : '10px' }}>
                  <span onClick={() => {handleSongClick(reco[4].songName); handleIdClick(reco[0].id);}}>
                    {reco[4].songName}
                  </span>
                  <p onClick={() => {handleSongClick(reco[4].songName); handleIdClick(reco[4].id);}} style={{ fontSize: '10px', textAlign: 'left' }} >{reco[4].artistName}</p>                  
                </div>
                </div>                
              </td>
              </tr> */}
            </tbody>
          </table>
        </div>
        {isModalVisible && selectedLyrics !== null && (
        <div className="lyric_modal">
          <h4>reco[index].songName 가사</h4>
          <p>{selectedLyrics}</p>
          <button onClick={closeModal}>닫기</button>
        </div>
      )}

    </div>


  
      
    );
  };

  // function Modal(props){

  //   return(
  //     <div className ="modal1">
  //       <p>{props.lyrics}</p>
  //       <button onClick={props.onClose}>닫기</button>        
