import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './home.css';

function DCNresult(props){
  const navigate = useNavigate();

  const [dcn, setDcn] = useState([]);
  const userId = props.userId;  
  const [selectedLyrics, setSelectedLyrics] = useState(null);
  const [selectedTitle, setSelectedTitle] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);      

    const getResult = async() => {
        try {
          const response = await axios.get(`http://localhost:8080/api/annoy/recommend-songs/${userId}`);
            console.log(response.data)
            setDcn(response.data);
        } catch (e) {
            console.log(e)
        }
    };

    useEffect(() => {
        getResult();
      }, []);

      const handleLyricClick = (index) => {
        setSelectedLyrics(reco[index].lyric_Str);
        setSelectedTitle(reco[index].songName);
        setIsModalVisible(true);
      };      

      const closeModal = () => {
        setIsModalVisible(false);
      };            

    return (
      
    <div className="cent" style={{ margin: "20px", width: "100%" }}>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <table className="cent">
            <tbody>
              <tr>
                <td colSpan="4" style={{ textAlign: "left" }}>
                  <p>'데이식스'를 좋아하는 당신에게</p>
                </td>
              </tr>
              <tr style={{ justifyContent: "center" }}>
                <td className="tdSize">
                  <img
                    src="image5.jpg"
                    alt="Image 5"
                    style={{ maxWidth: "180px", maxHeight: "180px" }}
                  />
                </td>
                <td className="tdSize">
                  <img
                    src="image6.jpg"
                    alt="Image 6"
                    style={{ maxWidth: "180px", maxHeight: "180px" }}
                  />
                </td>
                <td className="tdSize">
                  <img
                    src="image7.jpg"
                    alt="Image 7"
                    style={{ maxWidth: "180px", maxHeight: "180px" }}
                  />
                </td>
                <td className="tdSize">
                  <img
                    src="image8.jpg"
                    alt="Image 8"
                    style={{ maxWidth: "180px", maxHeight: "180px" }}
                  />
                </td>
              </tr>
            </tbody>
          </table>
        </div>
    </div>
      
    );
  };
  
  export default DCNresult;
