import React from 'react';
import { useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';


function Info(props){
  const [showurl, setShowurl] = useState([]);



return <>
<div style={{ display: 'flex', justifyContent: 'center', marginTop : '40px', fontSize : '18px' }}>
  <table>
  <tr>
  음악 추천 서비스를 제공하는 Watermelon 홈페이지 입니다.
  </tr>
  <tr>
  저희는 회원분들이 평소에 좋아하시는 노래의 가사, 평점, 주파수를 이용하여
  </tr>
  <tr>
  회원분들에게 노래를 추천드리는 서비스를 제공하고 있습니다.
  </tr>
  <tr>
  서비스를 이용하고 싶으시면 우측 상단의 회원가입 버튼을 이용하여 회원가입을 진행해주세요.
  </tr>
  </table>

  <Swiper
      spaceBetween={50}
      slidesPerView={3}
      onSlideChange={() => console.log('slide change')}
      onSwiper={(swiper) => console.log(swiper)}
    >
      <SwiperSlide>Slide 1</SwiperSlide>
      <SwiperSlide>Slide 2</SwiperSlide>
      <SwiperSlide>Slide 3</SwiperSlide>
      <SwiperSlide>Slide 4</SwiperSlide>
    </Swiper>

</div>

</>
}

export default Info;
