import React from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Recommend1 from './Recommend1';
import Recommend2 from './Recommend2';


function Signrecommend(props){
    const navigate = useNavigate();
    const [showAlert, setShowAlert] = useState(false); // 경고창 노출 여부 상태 추가  
    const closeAlert = () => {
        setShowAlert(false); // 경고창 닫기
      };          

    const [selectedItems, setSelectedItems] = useState(new Set());
    const selectedItemsArray = Array.from(selectedItems);

    const [selectedSongs, setSelectedSongs] = useState(new Set());
    const selectedSongsArray = Array.from(selectedSongs);

    const [selectedIds, setSelectedIds] = useState(new Set());    
    const selectedIdsArray = Array.from(selectedIds);    

    const axiosConfig = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const handleSelectedItemsChange = (items) => {
      setSelectedItems(new Set(items));
    };    

    const handleSelectedSongsChange = (songs) => {
      setSelectedSongs(new Set(songs));
    };

    const handleRecommendSubmit = (e) => {
        e.preventDefault();
        // 선택한 장르와 노래 목록을 서버로 전송
        const tags = {
          tag: JSON.stringify(selectedItemsArray),
          // tag : Array.from(selectedItems),
        };

        const songs = {
          songIds : JSON.stringify(selectedSongsArray),
          // songIds: Array.from(selectedSongs),
        }

        const userId = JSON.parse(localStorage.getItem('user')).id;       
        
        axios.post(`http://localhost:8080/api/song/post/${userId}`, songs, axiosConfig)
        .then(response => {   
          console.log(songs)
          console.log(response)
            if (response.status === 200) {
                navigate('/welcome');
                // 세션 스토리지에 아이디를 저장
              } else {
                let message = response.status;
                if (response.status === 400) {
                  message = "400 오류가 났습니다.";
                  console.log(response)
                }
                console.log(message);
              }
        })
        .catch(error => {
          console.log(songs)
          console.log(tags)
          console.log(error)
            setShowAlert(true); // 경고창 노출 상태 변경
        });        

        // axios.post(`http://localhost:8080/api/tag/post/${userId}`, tags, axiosConfig)
        // .then(response => {
        //   // console.log(tags)
        //     if (response.status === 200) {
        //         navigate('/welcome');
        //         // console.log(response)
        //         // 세션 스토리지에 아이디를 저장
        //       } else {
        //         let message = response.status;
        //         if (response.status === 400) {
        //           message = "400 오류가 났습니다.";
        //         }
        //         console.log(message);
        //       }
        // })
        // .catch(error => {
        //     console.log(error)
        //     setShowAlert(true);
        // });



    };        



return <>
<div>

{/* <img src="watermelon_logo.jpg" alt="Watermelon" style={{ width: '250px', height: 'auto', marginLeft: '394px', marginTop : '25px'}} />     */}

<Recommend1 setSelectedItems={handleSelectedItemsChange} />

<Recommend2 setSelectedSongs={handleSelectedSongsChange} />

<div style={{ display: 'flex', justifyContent: 'center', marginTop : '40px' }}>
<button className = 'submit-btn' onClick={handleRecommendSubmit}>전송</button>
</div>

</div>

{showAlert && (
        <div className="recommend-alert">
          <div className="recommend-alert-modal">
            <p style={{marginBottom : '20px'}}>예상치 못한 오류가 발생하였습니다.</p>
            <button onClick={closeAlert}>닫기</button>
          </div>
        </div>
      )}

</>
}

export default Signrecommend;
