import React from "react";

const Searchresult = ( { searchResults } ) => {
  // const searchData = location.state.data;
  const { resultdata1, resultdata2, resultdata3 } = searchResults;
  console.log(resultdata1);
  console.log(resultdata2);
  console.log(resultdata3);

  const handleLyricClick = (index) => {
    setSelectedLyrics(reco[index].lyric_Str);
    setSelectedTitle(reco[index].songName);
    setIsModalVisible(true);
  };       

  const closeModal = () => {
    setIsModalVisible(false);
  };        


  return (
    <div className="cent" style={{ margin: "20px", width: "100%" }}>
      <div style={{ display: "flex", justifyContent: "center" }}>
      <table className="td800">
      <h2>검색 결과</h2>
      <tbody>
        
        
        {resultdata1.length > 0 ? (
                <tr key={index} style={{ fontSize: "16px", textAlign: "left" }}>
                <td className="td400">
                  <div style={{ display: "flex", flexDirection: "row" }}>
                    <img
                      alt="song"
                      src={song.url}
                      width="50"
                      height="50"
                    />
                    <div style={{ marginLeft: "10px" }}>
                      <p
                        style={{ fontSize: "10px", textAlign: "left" }}
                      >
                        {song.artistName}
                      </p>
                    </div>
                  </div>
                </td>
                <td>
                <div style={{ marginLeft : "100px", display: "flex", flexDirection: "row" }}>
                  <button className = 'lyric-btn' onClick={() => handleLyricClick(index)} >
                  가사보기
                  </button>
                </div>
                </td>
              </tr>
        ) : (
          <div>No results</div>
        )}        
        {resultdata2.length > 0 ? (
          <div>
            
            {resultdata2.slice(0, 5).map((result, index) => (
              <li key={index}>
              <p>Song Name: {result.songName}</p>
              <p>Artist Name: {result.artistName}</p>
            </li>
            ))}
          </div>
        ) : (
          <div>No results</div>
        )}
        {resultdata3.length > 0 ? (
          <div>
            
            {resultdata3.slice(0, 5).map((result, index) => (
              <li key={index}>
              <p>Song Name: {result.songName}</p>
              <p>Artist Name: {result.artistName}</p>
            </li>
            ))}
          </div>
        ) : (
          <div>No results</div>
        )}        
      </tbody>
      </table>
      </div>

      {isModalVisible && selectedLyrics !== null && (
        <div className="lyric_modal">
          <div className="title_container">
            <h4>{selectedTitle}</h4>
          </div>
          <div className="lyric_content" dangerouslySetInnerHTML={{ __html: selectedLyrics }} />
          <button onClick={closeModal}>닫기</button>
        </div>
      )}
            
    </div>
  );
};

export default Searchresult;
