import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";

function Profile() {
  const [redirect, setRedirect] = useState(null);
  const [userReady, setUserReady] = useState(false);
  const [currentUser, setCurrentUser] = useState({ username: "" });

  useEffect(() => {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) {
      setRedirect("/home");
    } else {
      setCurrentUser(currentUser);
      setUserReady(true);
    }
  }, []);

  const handleUpdateClick = () => {
    // 예제로 회원정보 변경 페이지로 이동하도록 설정
    setRedirect("/"); // 원하는 경로로 이동합니다.
  };

  if (redirect) {
    return <Navigate to={redirect} />;
  }

  return (
    <div className="container" style={{ margin: "20px", width: "100%" }}>
      {userReady ? (
        <div>
          <header className="jumbotron">
            <div style={{ border: "1px solid #ccc", borderRadius: "10px", padding: "40px", width: "600px" }}>
              <div style={{ display: "flex", alignItems: "center" }}>
                <div>
                  <img
                    src={currentUser.profileImage || "//ssl.gstatic.com/accounts/ui/avatar_2x.png"}
                    alt="profile-img"
                    className="profile-img-card"
                  />
                </div>
                <div>
                  <h4>{currentUser.username}님 환영합니다!</h4>
                  <p style={{ color: "gray" }}>{" "}
                    {currentUser.email}</p>
                  <div style={{ display: "flex", gap: "10px" }}>
                    <button className="btn btn-profileg" onClick={handleUpdateClick}>
                      회원정보 변경
                    </button>
                    <button className="btn btn-profiler">회원정보 탈퇴</button>
                  </div>
                </div>
              </div>
              <div style={{ margin: "10px 0" }}>
                <hr style={{ border: "1px solid #ccc", width: "100%" }} />
              </div>
              <div>
                <p>회원님은<strong> 일반</strong> 유저입니다.</p>
              </div>
            </div>
          </header>
          <p>
            <strong>Token:</strong>{" "}
            {currentUser.accessToken.substring(0, 20)} ...{" "}
            {currentUser.accessToken.substr(currentUser.accessToken.length - 20)}
          </p>
          <p>
            <strong>Id:</strong>{" "}
            {currentUser.id}
          </p>
          <strong>Authorities:</strong>
          <ul>
            {currentUser.roles &&
              currentUser.roles.map((role, index) => <li key={index}>{role}</li>)}
          </ul>
        </div>
      ) : null}
    </div>
  );
}

export default Profile;
