import React, { useState, useEffect } from "react";
import {Nav} from 'react-bootstrap';
import UserService from "../services/user.service";
import Rankchart from './Rankchart';
import Tagresult from './Tagresult';
import Songresult from "./Songresult";

const Home = () => {
  const [content, setContent] = useState("");
  const [tab, setTab] = useState(0);

  useEffect(() => {
    UserService.getPublicContent().then(
      (response) => {
        setContent(response.data);
      },
      (error) => {
        setContent(
          (error.response && error.response.data) ||
            error.message ||
            error.toString()
        );
      }
    );
  }, []);

  return (
    <>
      <div style={{ display: 'flex', justifyContent: 'center', width : '1200px' }}>
      <Nav variant="tabs" defaultActiveKey="link0">
      <Nav.Item>
        <Nav.Link onClick={() => { setTab(0) }} eventKey="link0">선호하는 노래를 기반으로 추천</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link onClick={() => { setTab(1) }} eventKey="link1">선호하는 장르를 기반으로 추천</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link onClick={() => { setTab(2) }} eventKey="link2">추천3</Nav.Link>
      </Nav.Item>
    </Nav>
    </div>    
      {tab === 0 && <Songresult />} {/* Show Songresult component when tab is 0 */}
      {tab === 1 && <Tagresult />} {/* Show Tagresult component when tab is 1 */}
      {tab === 2 && <Rankchart />} {/* Show Rankchart component when tab is 2 */}


    </>
  );
};

export default Home;

