import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';

function Recommend2(props){
    // const navigate = useNavigate(); 
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isHovering, setIsHovering] = useState(false); // 마우스 커서 상태    
    const [selectedSongs, setSelectedSongs] = useState(new Set()); // 선택된 아이템 집합
    const [filteredData, setFilteredData] = useState([]); // 초기에 랜덤으로 20개의 아이템 선택    
    const [selectedIds, setSelectedIds] = useState(new Set());



    const fetchSongs = async() => {
        try {
            setData(null);
            const response = await axios.get('http://localhost:8080/api/newsong');
            setData(response.data); 
            setLoading(false);
        } catch (e) {
            setError(e);
            setLoading(false);
        }
    };

    useEffect(() => {
      fetchSongs();
  }, []);    

  useEffect(() => {
    console.log('Data:', data); // Log the data to check if it's fetched correctly
    if (data?.songName && data?.id) {
      const selectedIds = {};
      for (let i = 0; i < data.songName.length; i++) {
        selectedIds[data.songName[i]] = data.id[i];
      }
      setSelectedIds(selectedIds);
    }
  }, [data]);  


    useEffect(() => {
        setFilteredData(getRandomSongs(data, 10));
    }, [data]);

    useEffect(() => {
      props.setSelectedSongs(selectedSongs);
    }, [selectedSongs]);        

    useEffect(() => {
      props.setSelectedIds(selectedIds);
    }, [selectedIds]);           

    if (loading) return <div>로딩중..</div>;
    if (error) return <div>에러가 발생했습니다.</div>;
    if (!data) return null;

    const handleRandomize = () => {
      setFilteredData(getRandomSongs(data, 10)); // 새로운 랜덤 아이템으로 filteredData 업데이트
    };

    function getRandomSongs(arr, count) { 
        if (!arr || arr.length === 0) {
            return [];
        }
      const shuffled = arr.sort(() => 0.5 - Math.random()); // 배열을 랜덤하게 섞기
      return shuffled.slice(0, count); // 지정된 개수만큼의 아이템 선택
    } 

    const handleRemoveSongClick = (song) => {
      setSelectedSongs((prevSongs) => {
        const newSongs = new Set(prevSongs);
        newSongs.delete(song);
        return newSongs;
      });

      // setSelectedIds((prevIds) => {
      //   const newIds = new Set(prevIds);
      //   newIds.delete(song);
      //   return newIds;
      // });      
    };        

    const handleSongClick = (song) => {
      setSelectedSongs((prevSongs) => {
        const newSongs = new Set(prevSongs); // 이전 선택된 아이템 집합 복사
  
        if (newSongs.has(song)) {
          newSongs.delete(song); // 이미 선택된 아이템이면 선택 해제
        } else {
          if (newSongs.size >= 5) {
            return newSongs;
          }
          newSongs.add(song);
        }
  
        return newSongs;
      });

      setSelectedIds((prevIds) => {
        const newIds = new Set(prevIds);
        const songId = selectedIds[song];
        if (newIds.has(songId)) {
          newIds.delete(songId);
        } else {
          if (newIds.size >= 5) {
            return newIds;
          }
          newIds.add(songId);
        }
        return newIds;
      });  

      // setSelectedIds((prevIds) => {
      //   const newIds = new Set(prevIds);

      //   if (newIds.has(song)) {
      //     newIds.delete(song); // 이미 선택된 아이템이면 선택 해제
      //   } else {
      //     if (newIds.size >= 5) {
      //       return newIds;
      //     }
      //     newIds.add(song);
      //   }

      //   return newIds;
      // });



    };    




    // const [filteredData, setFilteredData] = useState(() => getRandomSongs(data, 10)); // 초기에 랜덤으로 20개의 아이템 선택         

      return <>

      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <table className = 'recommend2-container' >
          <tbody>
          <tr>
                  <td colSpan="1" style={{ textAlign: 'left' }}>
                    <p style = {{ fontSize : '24px', textAlign: 'left' }}>나는 이런 노래가 좋아요</p>
                  </td>

                  <td>
                  <button className = 'refresh-btn2' onClick={handleRandomize}>다른 노래 보기</button>
                  </td>
                  
            </tr>         
            <tr style={{ fontSize : '16px', textAlign: 'left' }}>
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}} >
                <img onClick={() => handleSongClick(filteredData[0]?.songName)} alt = "song" src={filteredData[0]?.url} width="50" height="50"/>                         
                <div style = {{ marginLeft : '10px' }}>
                  <span onClick={() => handleSongClick(filteredData[0]?.songName)}>
                    {filteredData[0]?.songName}
                  </span>
                  <p onClick={() => handleSongClick(filteredData[0]?.songName)} style={{ fontSize: '10px', textAlign: 'left' }} >{filteredData[0]?.artistName}</p>                  
                </div>
                </div>                
              </td>
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}} >
                  <img onClick={() => handleSongClick(filteredData[1]?.songName)} alt = "song" src={filteredData[1]?.url} width="50" height="50"/>                  
                  <div style = {{ marginLeft : '10px' }} >
                  <span onClick={() => handleSongClick(filteredData[1]?.songName)}>
                    {filteredData[1]?.songName}
                  </span>
                  <p onClick={() => handleSongClick(filteredData[1]?.songName)} style={{ fontSize: '10px', textAlign: 'left' }} >{filteredData[1]?.artistName}</p>
                </div>
                </div>
              </td>
              <td></td><td></td>
              </tr>
              <tr>
              <td className="td400">
              <div style={{display : 'flex', flexDirection: 'row'}} >
                <img onClick={() => handleSongClick(filteredData[2]?.songName)} alt = "song" src={filteredData[2]?.url} width="50" height="50"/>
                <div style = {{ marginLeft : '10px' }} >
                  <span onClick={() => handleSongClick(filteredData[2]?.songName)}>
                    {filteredData[2]?.songName}
                  </span>
                  <p onClick={() => handleSongClick(filteredData[2]?.songName)} style={{ fontSize: '10px', textAlign: 'left' }} >{filteredData[2]?.artistName}</p>                  
                </div>
                </div>
              </td>
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}} >
                <img onClick={() => handleSongClick(filteredData[3]?.songName)} alt = "song" src={filteredData[3]?.url} width="50" height="50"/>
                <div style = {{ marginLeft : '10px' }} >
                  <span onClick={() => handleSongClick(filteredData[3]?.songName)}>
                    {filteredData[3]?.songName}
                  </span>
                  <p onClick={() => handleSongClick(filteredData[3]?.songName)} style={{ fontSize: '10px', textAlign: 'left' }} >{filteredData[3]?.artistName}</p>                  
                </div>
                </div>
              </td>
              <td></td><td></td>
            </tr>
            <tr style={{ fontSize : '16px', textAlign: 'left' }}>
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}}>
                <img onClick={() => handleSongClick(filteredData[4]?.songName)} alt = "song" src={filteredData[4]?.url} width="50" height="50"/>
                <div style = {{ marginLeft : '10px' }} >
                  <span onClick={() => handleSongClick(filteredData[4]?.songName)}>
                    {filteredData[4]?.songName}
                  </span>
                  <p onClick={() => handleSongClick(filteredData[4]?.songName)} style={{ fontSize: '10px', textAlign: 'left' }} >{filteredData[4]?.artistName}</p>                  
                </div>
                </div>
              </td>
              <td className="td400">
                <div style={{display : 'flex', flexDirection: 'row'}} >
                <img onClick={() => handleSongClick(filteredData[5]?.songName)} alt = "song" src={filteredData[5]?.url} width="50" height="50"/>
                <div style = {{ marginLeft : '10px' }} >
                  <span onClick={() => handleSongClick(filteredData[5]?.songName)}>
                    {filteredData[5]?.songName}
                  </span>
                  <p onClick={() => handleSongClick(filteredData[5]?.songName)} style={{ fontSize: '10px', textAlign: 'left' }} >{filteredData[5]?.artistName}</p>                                    
                </div>
                </div>
              </td>
              <td></td><td></td>
              </tr>
              {/* <tr>
              <td className="td400">
              <div style={{display : 'flex', flexDirection: 'row'}} >
                <img onClick={() => handleSongClick(filteredData[6]?.song_name)} src={filteredData[6]?.url} width="50" height="50"/>
                <div style = {{ marginLeft : '10px' }} >
                  <span onClick={() => handleSongClick(filteredData[6]?.song_name)}>
                    {filteredData[6]?.song_name}
                  </span>
                  <p onClick={() => handleSongClick(filteredData[6]?.song_name)} style={{ fontSize: '10px', textAlign: 'left' }} >{filteredData[6]?.artist_name}</p>                                                      
                </div>
                </div>
              </td>
              <td className="td400">
              <div style={{display : 'flex', flexDirection: 'row'}} >
                <img onClick={() => handleSongClick(filteredData[7]?.song_name)} src={filteredData[7]?.url} width="50" height="50"/>
                <div style = {{ marginLeft : '10px' }} >
                  <span onClick={() => handleSongClick(filteredData[7]?.song_name)}>
                    {filteredData[7]?.song_name}
                  </span>
                  <p onClick={() => handleSongClick(filteredData[7]?.song_name)} style={{ fontSize: '10px', textAlign: 'left' }} >{filteredData[7]?.artist_name}</p>                                                      
                </div>
                </div>
              </td>
              <td></td><td></td>
            </tr> */}
            <tr>
            <td colSpan="4" style={{ textAlign: 'left'}}>
                  <p style={{ fontSize: '16px', textAlign: 'left' }}> 
                  {Array.from(selectedSongs).map((song, index) => (
                      <span className="selected-item2" key={index}
                      onMouseEnter={() => setIsHovering(true)}
                      onMouseLeave={() => setIsHovering(false)}
                      onClick={() => handleRemoveSongClick(song)}                      
                      >{song} {isHovering && (
                        <span className="remove-icon" onClick={() => handleRemoveSongClick(song)}></span>
                      )}</span>
              ))}</p>                
                  </td>            
            </tr>                  
          </tbody>
        </table>
      </div>      
      
      </>      

}

export default Recommend2;
