import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';

function Recommend1(props){
    // const navigate = useNavigate();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isHovering, setIsHovering] = useState(false); // 마우스 커서 상태
    const [selectedItems, setSelectedItems] = useState(new Set()); // 선택된 아이템 집합
    const [filteredData, setFilteredData] = useState([]); // 초기에 랜덤으로 n개의 아이템 선택

    const fetchTags = async () => {
        try {
            setData(null);
            const response = await axios.get('http://localhost:8080/api/tag');
            setData(response.data);
            setLoading(false);
        } catch (e) {
            setError(e);
            setLoading(false);
        }
    };
    useEffect(() => {
        fetchTags();
    }, []);

    useEffect(() => {
        setFilteredData(getRandomItems(data, 8));
    }, [data]);

    if (loading) return <div>로딩중..</div>;
    if (error) return <div>에러가 발생했습니다.</div>;
    if (!data) return null;    

    const handleRandomize = () => {
      setFilteredData(getRandomItems(data, 8)); // 새로운 랜덤 아이템으로 filteredData 업데이트
    };

    function getRandomItems(arr, count) {
        if (!arr || arr.length === 0) {
            return [];
        }
      const shuffled = arr.sort(() => 0.5 - Math.random()); // 배열을 랜덤하게 섞기
      return shuffled.slice(0, count); // 지정된 개수만큼의 아이템 선택
    };

    const handleRemoveItemClick = (item) => {
      setSelectedItems((prevItems) => {
        const newItems = new Set(prevItems);
        newItems.delete(item);
        return newItems;
      });
    };

    const handleItemClick = (item) => {
      setSelectedItems((prevItems) => {
        const newItems = new Set(prevItems); // 이전 선택된 아이템 집합 복사
  
        if (newItems.has(item)) {
          newItems.delete(item); // 이미 선택된 아이템이면 선택 해제
        } else {
          if (newItems.size >= 5) {
            return newItems;
          }
          newItems.add(item);
        }
  
        return newItems;
      });
    };    

      return <>
      
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <table className = 'recommend1-container' >
          <tbody >
          <tr>
                  <td colSpan="2" style={{ textAlign: 'left' }}>
                    <p style = {{ fontSize : '24px', textAlign: 'left' }}>나는 이런 장르가 좋아요</p>
                  </td>

                  <td>
                  <button className = 'refresh-btn1' onClick={handleRandomize}>새로고침</button>
                  </td>
            </tr>    
            <tr style={{ fontSize : '16px', textAlign: 'left' }}>
              <td className="td200">
                <div>
                  <span className='select-item1' 
                  style={{ background: 'linear-gradient(to right, #fe4452, #F5F5F5)' }} onClick={() => handleItemClick(filteredData[0]?.songName)}>
                    {filteredData[0]?.songName}
                  </span>
                </div>
              </td>
              <td className="td200">
                <div>
                  <span className='select-item1' onClick={() => handleItemClick(filteredData[1]?.songName)}>
                    {filteredData[1]?.songName}
                  </span>
                </div>
              </td>
              <td className="td200"> 
                <div>
                  <span className='select-item1' onClick={() => handleItemClick(filteredData[2]?.songName)}>
                    {filteredData[2]?.songName}
                  </span>
                </div>
              </td>
              <td className="td200">
                <div>
                  <span className='select-item1' onClick={() => handleItemClick(filteredData[3]?.songName)}>
                    {filteredData[3]?.songName}
                  </span>
                </div>
              </td>
            </tr>
            <tr style={{ fontSize : '16px', textAlign: 'left' }}>
              <td className="td200">
                <div>
                  <span className='select-item1' onClick={() => handleItemClick(filteredData[4]?.songName)}>
                    {filteredData[4]?.songName}
                  </span>
                </div>
              </td>
              <td className="td200">
                <div>
                  <span className='select-item1' onClick={() => handleItemClick(filteredData[5]?.songName)}>
                    {filteredData[5]?.songName}
                  </span>
                </div>
              </td>
              <td className="td200">
                <div>
                  <span className='select-item1' onClick={() => handleItemClick(filteredData[6]?.songName)}>
                    {filteredData[6]?.songName}
                  </span>
                </div>
              </td>
              <td className="td200">
                <div>
                  <span className='select-item1' onClick={() => handleItemClick(filteredData[7]?.songName)}>
                    {filteredData[7]?.songName}
                  </span>
                </div>
              </td>
            </tr>    
            <tr>
          <td colSpan="4" style={{ textAlign: 'left' }}>
            <p style={{ fontSize: '16px', textAlign: 'left' }}>
               {Array.from(selectedItems).map((item, index) => (
                <span className="selected-item1" key={index}
                onMouseEnter={() => setIsHovering(true)}
                onMouseLeave={() => setIsHovering(false)}
                onClick={() => handleRemoveItemClick(item)}
                >{item} {isHovering && (
                  <span className="remove-icon" onClick={() => handleRemoveItemClick(item)}></span>
                )}</span>
              ))}
            </p>                   
          </td>
          </tr>               
          </tbody>
        </table>
      </div>

      </>

}

export default Recommend1;
