import React, { useState, useEffect } from "react";
import {Nav} from 'react-bootstrap';
import UserService from "../services/user.service";
import Rankchart from './Rankchart';
import Tagresult from './Tagresult';
import Songresult from "./Songresult";
import "./home.css";

const Home = () => {
  const [content, setContent] = useState("");
  const [tab, setTab] = useState(0);

  const userId = JSON.parse(localStorage.getItem('user'))?.id;

  useEffect(() => {
    UserService.getPublicContent().then(
      (response) => {
        setContent(response.data);
      },
      (error) => {
        setContent(
          (error.response && error.response.data) ||
            error.message ||
            error.toString()
        );
      }
    );
  }, []);

  return (
    <>
    <div style={{ display: 'flex', justifyContent: 'center' }}>
      <div className = "navbar1" style={{ display: 'flex', justifyContent: 'center' }}>

    (

      <Nav className = "nav-tab1" variant="tabs" defaultActiveKey="link0">
      <Nav.Item>
        <Nav.Link onClick={() => { setTab(0) }} eventKey="link0" className={tab === 0 ? "nav-link1 active" : "nav-link1"} >당신이 좋아할만한 노래</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link onClick={() => { setTab(1) }} eventKey="link1"className={tab === 1 ? "nav-link1 active" : "nav-link1"} >선호하는 장르를 기반으로 추천</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link onClick={() => { setTab(2) }} eventKey="link2" className={tab === 2 ? "nav-link1 active" : "nav-link1"} >선호하는 노래를</Nav.Link>
      </Nav.Item>
    </Nav>
    </div>
    </div>
      {tab === 0 && <Songresult userId={userId} />}
      {tab === 1 && <Tagresult userId={userId} />}
      {tab === 2 && <Rankchart userId={userId} />}

      ):(
                <div style={{ display: "flex", justifyContent: "center" }} >
                  로그인을 하셔야 이용하실 수 있는 서비스입니다.
                </div>
              )      


    </>
  );
};

export default Home;
