import React, { useState, useEffect } from 'react';

const Player = () => {
  const [songplaying, setSongPlaying] = useState(false);

  const song = new Audio("https://www.youtube.com/watch?v=vRxtlMnmzDQ");

  useEffect(() => {
    const handleSongEnd = () => {
      setSongPlaying(false);
    };

    song.addEventListener("ended", handleSongEnd);

    return () => {
      song.removeEventListener("ended", handleSongEnd);
    };
  }, [song]);

  const switchPlay = () => {
    setSongPlaying(prevState => !prevState, () => {
      songplaying ? song.play() : song.pause();
    });
  };

  return (
    <div>
      <button onClick={switchPlay}>
        {songplaying ? "Pause" : "Play"}
      </button>
    </div>
  );
};

export default Player;
